mapper.select:
select * from (select rownum rn, e.* from (
select * from display where del = 'n' order by dno desc) e)
where rn between #{startRow} and #{endRow}

mapper.update:
update display set dname=#{dname}, start_date=#{start_date}, end_date=#{end_date}, hours=#{hours}, spot=#{spot}, loc=#{loc}, address=#{address}, fee=#{fee,jdbcType=INTEGER}, discount=#{discount,jdbcType=INTEGER}, fee_adult=#{fee_adult,jdbcType=INTEGER}, fee_teen=#{fee_teen,jdbcType=INTEGER}, fee_child=#{fee_child,jdbcType=INTEGER}, intro=#{intro}, tag=#{tag,jdbcType=VARCHAR}, artist=#{artist,jdbcType=VARCHAR}, tel=#{tel,jdbcType=VARCHAR}, home_pg=#{home_pg,jdbcType=VARCHAR}, detail_txt=#{detail_txt,jdbcType=VARCHAR}
,detail_img = #{detail_img}
,poster = #{poster}
where dno = #{dno}

mapper.select:
select * from (select rownum rn, e.* from (
select * from display where loc in

	#{item}

and del = 'n' order by dno desc) e)
where rn between #{startRow} and #{endRow}

select * from (select rowNum rn, e.* from (
select * from display where mno = #{mno} and del = 'n' order by dno desc) e)
where rn between #{startRow} and #{endRow}

select * from (select rowNum rn, e.* from (select * from display order by
dno desc) e)
where rn between #{startRow} and #{endRow}

select count(*) from display where loc in

	#{item}

and del = 'n' order by dno desc

select * from (select rownum rn, e.* from ( select * from display where
tag like '%'||#{item}||'%'
and del = 'n' order by dno desc) e) where rn between #{startRow} and #{endRow}
